import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Header.css';
import UnleashedLogo from '../assets/UnleashedTeam.png';

function Header({ isLoggedIn, userName, setIsLoggedIn, setUser }) {
  const [showLogoutPrompt, setShowLogoutPrompt] = useState(false);
  const navigate = useNavigate();

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUser({ username: 'Guest', role: 'guest' });
    localStorage.removeItem('isLoggedIn');
    localStorage.removeItem('user');
    localStorage.removeItem('user_id');
    setShowLogoutPrompt(false);
    navigate('/');
  };

  const handleCancelLogout = () => {
    setShowLogoutPrompt(false);
  };

  const handleLogin = () => {
    navigate('/login');
  };

  return (
    <>
      <header className="header">
        <div className="header-left">
          <Link to="/" className="logo-link">
            <img src={UnleashedLogo} alt="Logo" className="logo-image" />
            <span className="logo-text">UnleashedAI</span>
          </Link>
        </div>
        <nav className="nav">
          <Link to="/" className="nav-link">Home</Link>
          <Link to="/chatbot" className="nav-link">Chatbot</Link>
          {/* <Link to="/history" className="nav-link">History</Link> */}
          <Link to="/about" className="nav-link">About Us</Link>
          {isLoggedIn && userName.role === 'admin' && (
            <Link to="/admin" className="nav-link">Admin</Link>
          )}
        </nav>
        <div className="auth-buttons">
          {!isLoggedIn ? (
            <button className="sign-in" onClick={handleLogin}>Sign in</button>
          ) : (
            <>
              <span className="user-name">Hi, {userName?.username}</span>
              <button
                className="logout-button"
                onClick={() => setShowLogoutPrompt(true)}
              >
                Logout
              </button>
            </>
          )}
        </div>
      </header>

      {/* Logout Prompt Modal */}
      {showLogoutPrompt && (
        <div className="logout-modal">
        <div className="logout-modal-content">
          <h2>Are you sure you want to log out?</h2>
          <div className="logout-buttons">
            <button className="logout-confirm" onClick={handleLogout}>
              Yes
            </button>
            <button className="logout-cancel" onClick={handleCancelLogout}>
              No
            </button>
          </div>
        </div>
      </div>
      )}
    </>
  );
}

export default Header;
